          console.log("error")

          //console.log(String("https://api.opensea.io/api/v1/asset/" + dataResults["contractId" + i] + "/" + dataResults["tokenId" + i]))
          //increment counter of i but not b so it updates all picture frames instead of skipping
         i++;
         // count errors and add back to number of iterations
         error_counter = error_counter + 1


