//let script_log1 = parcel.getFeatureById('script_log1');
let tokenURI = "https://www.cryptovoxels.com/p/" + parcel.id;
let pLength = 0;
let pWidth = 0;
let pHeight = 0;

fetch('https://api.thegraph.com/subgraphs/name/protofire/cryptovoxels', {
	method: 'POST',
	headers: {
		'Content-Type': 'application/json',
	},
  body: JSON.stringify({
  	query: `
    	query MyQuery {
      parcels(first: 1, where: {tokenURI: "${tokenURI}" }) {
      id
      tokenID
      location
      buildHeight
      area
      length
      width
      volumeInVoxels
      owner {
      	id
      }
      createdAt
      tokenURI
       }
      }
    `,
    })
  })
	.then((res) => res.json())
  .then((result) => {
//		console.log(result.data);
		pLength = result.data.parcels[0].length;
		pWidth = result.data.parcels[0].width;
    pHeight = result.data.parcels[0].buildHeight;
		console.log(pLength);
		console.log(pWidth);
    console.log(pHeight);


let max_height = pHeight;
let offSet = 3;
let elementHeight = 1;
let elementSpacing = 1;
let timeShift = 200;
let altD = 0.5;


let max_colors = 400;
let colors = [];
for (let i=1; i < max_colors + 1; i++) {
  colors.push(rainbow(max_colors, i));
}

//9, 1, 0.33, 0.5, 0.5, 8
//-8.5, 1, 0.33, 0.5, 0.5, 8
//0.25, 1, 4, 17 0.5 0.5

//13 Kind Fork
//4, 1, 0.25 20
//-3.5, 1, 0.25
//0.25, 1, -9.5
//0.25, 1, 10

let westX = (pLength / 2) * (-1) + altD;
let westY = 1;
let westZ = 0.45;
let eastX = pLength / 2;
let eastY = 1;
let eastZ = 0.45;
let weSZ = pWidth - 1;

let northX = 0.25;
let northY = 1;
let northZ = pWidth / 2;
let southX = 0.25;
let southY = 1;
let southZ = (pWidth / 2) * (-1) + altD;
let nsSZ = pLength;




let wWalls = [];
let nWalls = [];
let eWalls = [];
let sWalls = [];
let localSpacing = elementSpacing;
let newFeature = null;
for (i=max_height;i>-1;i--) {
  //if (i < (max_height)) { localSpacing = elementSpacing; } else { localSpacing = 0; }
  console.log(localSpacing);
  newFeature = parcel.createFeature('cube');
  newFeature.set({color: '#000000'});
  newFeature.set({scale:[altD,elementHeight,weSZ]});
  newFeature.set({position:[westX,offSet + ((elementHeight + localSpacing) * i),westZ]});
  newFeature.set({collidable: true});
  newFeature.set({id: cvFeatureId('wWall', i) });
  //script_log1.set({text: newFeature.id});
  wWalls.push(newFeature);

  newFeature = parcel.createFeature('cube');
  newFeature.set({color: '#000000'});
  newFeature.set({scale:[nsSZ,elementHeight,altD]});
  newFeature.set({position:[northX,offSet + ((elementHeight + localSpacing) * i),northZ]});
  newFeature.set({collidable: true});
  newFeature.set({id: cvFeatureId('nWall', i) });
  //script_log1.set({text: newFeature.id});
  nWalls.push(newFeature);

  newFeature = parcel.createFeature('cube');
  newFeature.set({color: '#000000'});
  newFeature.set({scale:[altD,elementHeight,weSZ]});
  newFeature.set({position:[eastX,offSet + ((elementHeight + localSpacing) * i),eastZ]});
  newFeature.set({collidable: true});
  newFeature.set({id: cvFeatureId('eWall', i) });
  //script_log1.set({text: newFeature.id});
  eWalls.push(newFeature);

  newFeature = parcel.createFeature('cube');
  newFeature.set({color: '#000000'});
  newFeature.set({scale:[nsSZ,elementHeight,altD]});
  newFeature.set({position:[southX,offSet + ((elementHeight + localSpacing) * i),southZ]});
  newFeature.set({collidable: true});
  newFeature.set({id: cvFeatureId('sWall', i) });
  //script_log1.set({text: newFeature.id});
  sWalls.push(newFeature);
  //console.log(i);
}


let lwWall = null;
let lnWall = null;
let leWall = null;
let lsWall = null;
let lColor = null;
setInterval(()=>{
  lwWall = wWalls.pop();
  lnWall = nWalls.pop();
  leWall = eWalls.pop();
  lsWall = sWalls.pop();
  lColor = colors.pop();
  lwWall.set({ color: lColor});
  lnWall.set({ color: lColor});
  leWall.set({ color: lColor});
  lsWall.set({ color: lColor});
//script_log1.set({text: lColor});
//script_log1.set({text: lwWall.id});
  wWalls.unshift(lwWall);
  nWalls.unshift(lnWall);
  eWalls.unshift(leWall);
  sWalls.unshift(lsWall);
  colors.unshift(lColor);
}, timeShift);

});

function cvFeatureId(name, number) {
  return name + number.toString().padStart(2,'0');
}

function rainbow(numOfSteps, step) {
    // based on http://stackoverflow.com/a/7419630
    // This function generates vibrant, "evenly spaced" colours (i.e. no clustering). This is ideal for creating easily distiguishable vibrant markers in Google Maps and other apps.
    // Adam Cole, 2011-Sept-14
    // HSV to RBG adapted from: http://mjijackson.com/2008/02/rgb-to-hsl-and-rgb-to-hsv-color-model-conversion-algorithms-in-javascript
    var r, g, b;
    var h = step / numOfSteps;
    var i = ~~(h * 6);
    var f = h * 6 - i;
    var q = 1 - f;
    switch(i % 6){
        case 0: r = 1, g = f, b = 0; break;
        case 1: r = q, g = 1, b = 0; break;
        case 2: r = 0, g = 1, b = f; break;
        case 3: r = 0, g = q, b = 1; break;
        case 4: r = f, g = 0, b = 1; break;
        case 5: r = 1, g = 0, b = q; break;
    }

    var c = "#" + ("00" + (~ ~(r * 235)).toString(16)).slice(-2) + ("00" + (~ ~(g * 235)).toString(16)).slice(-2) + ("00" + (~ ~(b * 235)).toString(16)).slice(-2);
    return (c);
}
