// npc.js

setInterval(() => {
  let p = parcel.getPlayers()[0]

  if (!p) {
    return
  }

  let v1 = feature.position
  let v2 = p.position
  let angle = Math.atan2(v1.z - v2.z, v1.x - v2.x)

  feature.rotation.y = Math.PI / -2 - angle
}, 200)

function moveObject(object, player, dist, height){ // calculates new position of object

  let spherePos = object.position;
  let playerRotation = player.rotation;

  let xDelta = dist * Math.cos(-playerRotation.y+Math.PI/2 + Math.PI);
  let yDelta = dist * Math.tan(playerRotation.x);
  let zDelta = dist * Math.sin(-playerRotation.y+Math.PI/2 + Math.PI);

      var positionRell = [
    player.position.x - xDelta,
    player.position.y - yDelta + (height),
    player.position.z - zDelta
  ];

return positionRell
}


feature.on("click", (e) => {
  var vox_url = "https://media-crvox.sfo2.digitaloceanspaces.com/0x7a44d904e5372f64db51436fcdf4024a8144c383/1625852123667-7d52b624-ab7a-4f86-835d-443b13a2cced.vox";
  var text = parcel.createFeature("sign");
  var dialog = parcel.createFeature("vox-model");
  dialog.set({"url": vox_url})

  text.set({"text": " Hello " + e.player.name + ", How can I help you."});
  //  text.set({"position": [e.player.position.x, e.player.position.y, e.player.position.z]});
  text.set({"position": moveObject(text,e.player, 1, 0.5)})
  dialog.set({"position": moveObject(text,e.player, 1.1, 0.3)})
  text.set({"rotation": [0, e.player.rotation.y, 0]});
  dialog.set({"rotation": [0, e.player.rotation.y, 0]});
  text.set({"scale": [1, 0.2]});
  dialog.set({"scale": [2, 0.5, 0]});

  text.set({'color':"#000000"})
  text.set({'background':"#FFFFFF"})
  text.set({'fontSize':10})
  dialog.on("click", (e) => {
    text.remove();
    dialog.remove();
  })
  text.on("click", (e) => {
    text.remove();
    dialog.remove();
  })
  setTimeout(()=>{
    text.remove();
    dialog.remove();
  }, 3000);

})

