function setPosition(f, position) {
    f.position.x = position.x
    f.position.y = position.y
    f.position.z = position.z
}

function setRotation(f, rotation) {
    f.rotation.x = rotation.x
    f.rotation.y = rotation.y
    f.rotation.z = rotation.z
}

function minimiseRotation(rotationDelta) {
    return ((((rotationDelta/Math.PI)+1)%2)-1)*Math.PI

    // basically does this
    /*
    if (dR > Math.PI) {
        // eg yA = 1.8pi, y = .1pi, dR=yA-y=1.7pi, but would be better as -1.8pi -2pi - .1pi = -0.3pi
        dR -= 2*Math.PI
    } else if (dR < -Math.PI){
        // what about the other way, yA = 0.1, y = 1.8, yA-y = -1.7
        dR += 2*Math.PI
    }
    return dR
    */
}

function caclulateDistanceMovedAlongAxes(speed, currentPosition, destinationPosition) {

    let dx = destinationPosition.x - currentPosition.x
    let dy = destinationPosition.y - currentPosition.y
    let dz = destinationPosition.z - currentPosition.z

    let dist = Math.sqrt(dx*dx+dy*dy+dz*dz)

    return new Vector3(speed*dx/dist, speed*dy/dist, speed*dz/dist)
}

// move feature f to (x,y,z) in s seconds
function animatedMove(f, newPosition, s) {

    let positionDelta = new Vector3(
        newPosition.x - f.position.x,
        newPosition.y - f.position.y,
        newPosition.z - f.position.z
    )

    let newRotation = new Vector3(
        0,  
        Math.atan2(positionDelta.x, positionDelta.z)  + Math.PI / 2, 
        0
    )

    // some limited pitch adjustments if the destination is significantly up or down
    // could probably be refactored into one line hey
    if (positionDelta.y > 0.2) {
        if (positionDelta.y  > 1) {
            newRotation.z = -Math.PI/3 
        }
        else {
            newRotation.z = -Math.PI/8
        }
    } 
    else if (positionDelta.y < -0.2) {
        if (positionDelta.y < -1) {
            newRotation.z = Math.PI/3 
        }
        else {
            newRotation.z = Math.PI/8
        }
    }

    let rotationDelta = new Vector3(
        minimiseRotation(newRotation.x - f.rotation.x),
        minimiseRotation(newRotation.y - f.rotation.y),
        minimiseRotation(newRotation.z - f.rotation.z)
    )


    let animation = f.createAnimation('position')
    animation.setKeys([{
        frame: s*30, // standard is 30 fps (means it take 1 second)
        value: f.position.add( positionDelta )
    }])

    let animation2 = f.createAnimation('rotation')
    animation2.setKeys([{
        frame: s*30, // standard is 30 fps (means it take 1 second)
        value: f.rotation.add( rotationDelta )
    }])

    f.startAnimations( [animation, animation2] )

    setPosition(f, newPosition)  
    setRotation(f, newRotation)
}

setInterval(()=>{
    let target = parcel.getFeatureById(movingTargetFeatureID)
    if (!target) {
      return
    }

    let weMoveThisFar = caclulateDistanceMovedAlongAxes(speed, feature.position, target.position)

    let newPosition = new Vector3(
        feature.position.x + weMoveThisFar.x, 
        feature.position.y + weMoveThisFar.y, 
        feature.position.z + weMoveThisFar.z,
    )

    animatedMove(feature, newPosition, 1)


},1000)

